INSERT INTO `cc_crm_auto_responder` VALUES (1,1,'<div>\r\n	<div>\r\n		Hi,</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		Thank you for your interest in 500Hrs! We will soon introduce the application process and update you about important dates.</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		Feel free to email us with your queries at 500Hrs@causecode.com.</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		Best,</div>\r\n	<div>\r\n		Team CauseCode</div>\r\n</div>\r\n<div>\r\n	&nbsp;</div>\r\n','2013-09-04 16:23:45','2013-09-04 16:23:45','[500Hrs]: Thank you for your interest!','com.cc.crm.autoresponder.AutoResponder',NULL,'onAdd',NULL,'\0',NULL,'\0',NULL,'\0',NULL,'\0',NULL,'','',NULL,'[500Hrs]: Thank you for your interest!',NULL),(2,1,'<div>\r\n	<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n		<span id=\"docs-internal-guid-5a81fdcf-4ada-ff7a-b2e9-036d9ead8491\"><span style=\"font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertical-align: baseline; white-space: pre-wrap;\">Hi %firstName%,</span></span></p>\r\n	<br />\r\n	<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n		<span id=\"docs-internal-guid-5a81fdcf-4ada-ff7a-b2e9-036d9ead8491\"><span style=\"font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertical-align: baseline; white-space: pre-wrap;\">How are you doing? How is it going with %organizationName%? </span></span></p>\r\n	<br />\r\n	<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n		<span id=\"docs-internal-guid-5a81fdcf-4ada-ff7a-b2e9-036d9ead8491\"><span style=\"font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertical-align: baseline; white-space: pre-wrap;\">I am writing to introduce 500Hrs, a development incubator for startups. Applications open on 21st October.</span></span></p>\r\n	<br />\r\n	<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n		<span id=\"docs-internal-guid-5a81fdcf-4ada-ff7a-b2e9-036d9ead8491\"><span style=\"font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertical-align: baseline; white-space: pre-wrap;\">Details about the program:</span></span></p>\r\n	<ul style=\"margin-top:0pt;margin-bottom:0pt;\">\r\n		<li dir=\"ltr\" style=\"list-style-type: disc; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertical-align: baseline;\">\r\n			<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n				<span id=\"docs-internal-guid-5a81fdcf-4ada-ff7a-b2e9-036d9ead8491\"><span style=\"background-color: transparent; vertical-align: baseline; white-space: pre-wrap;\">Application fee: $99 Early bird, $199 Regular</span></span></p>\r\n		</li>\r\n		<li dir=\"ltr\" style=\"list-style-type: disc; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertical-align: baseline;\">\r\n			<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n				<span id=\"docs-internal-guid-5a81fdcf-4ada-ff7a-b2e9-036d9ead8491\"><span style=\"background-color: transparent; vertical-align: baseline; white-space: pre-wrap;\">A total of 500 hours of development/consulting time to build the MVP</span></span></p>\r\n		</li>\r\n		<li dir=\"ltr\" style=\"list-style-type: disc; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertical-align: baseline;\">\r\n			<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n				<span id=\"docs-internal-guid-5a81fdcf-4ada-ff7a-b2e9-036d9ead8491\"><span style=\"background-color: transparent; vertical-align: baseline; white-space: pre-wrap;\">No equity taken</span></span></p>\r\n		</li>\r\n		<li dir=\"ltr\" style=\"list-style-type: disc; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; vertic