INSERT INTO `cc_jobboard_job_post` VALUES (1,1,'2013-10-26 00:00:00','1','2013-08-26 15:43:09','<p style=\"color: rgb(68, 68, 68); font-family: ff-meta-web-pro-1, ff-meta-web-pro-2, arial, sans-serif; font-size: 16px;\">\r\n	We are always on lookout for talented and passionate individuals to join our team. In this position, you will get an opportunity to build prototypes for multiple startups. If you love coming up with interesting ideas, learning new technology and love anything web/mobile this would be a great fit for you.</p>\r\n<ul style=\"color: rgb(68, 68, 68); font-family: ff-meta-web-pro-1, ff-meta-web-pro-2, arial, sans-serif; font-size: 16px;\">\r\n	<li>\r\n		<p>\r\n			BE/BTech in Computer Science/IT</p>\r\n	</li>\r\n</ul>\r\n<ul style=\"color: rgb(68, 68, 68); font-family: ff-meta-web-pro-1, ff-meta-web-pro-2, arial, sans-serif; font-size: 16px;\">\r\n	<li>\r\n		<p>\r\n			Strong understanding of object oriented concepts</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			0-2+ years of experience using Java, Servlets, Hibernate, CSS, Javascript, HTML</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			jQuery, AJAX, REST, oAuth 2.0</p>\r\n	</li>\r\n</ul>\r\n<ul style=\"color: rgb(68, 68, 68); font-family: ff-meta-web-pro-1, ff-meta-web-pro-2, arial, sans-serif; font-size: 16px;\">\r\n	<li>\r\n		<p>\r\n			Strong understanding of cross browser and cross O/S design issues</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Strong verbal and written communication skills</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Prefer experience in working with off-shore clients and using online communication tools</p>\r\n	</li>\r\n</ul>\r\n<ul style=\"color: rgb(68, 68, 68); font-family: ff-meta-web-pro-1, ff-meta-web-pro-2, arial, sans-serif; font-size: 16px;\">\r\n	<li>\r\n		High energy, strong work ethic</li>\r\n</ul>\r\n','2013-08-26 15:44:31','Pune','Software Engineer',0),(2,5,'2014-10-27 00:00:00','1','2014-09-27 09:37:30','<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">We are looking for a sales executive with experience in the web/mobile technology space.</span></span></p>\r\n<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial; font-size: 15px; white-space: pre-wrap; line-height: 1.15; background-color: transparent;\">This is an opportunity for you to help us make a greater difference. At the core, we are organization that loves building products that solves user pain. We&rsquo;ve built applications in healthcare, social network, ecommerce and more. </span></p>\r\n<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial; font-size: 15px; white-space: pre-wrap; line-height: 1.15; background-color: transparent;\">Your role will be to connect and close deals with enterprises and startups that are looking for a technology partner to build a web or mobile based product. </span></p>\r\n<h2>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Trebuchet MS\'; font-size: 17px; white-space: pre-wrap; line-height: 1.15; background-color: transparent;\">Responsibilities May Include</span></h2>\r\n<ol style=\"margin-top:0pt;margin-bottom:0pt;\">\r\n	<li dir=\"ltr\" style=\"list-style-type: decimal; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent;\">\r\n		<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n			<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">Active involvement with the local startup and tech community</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\" style=\"list-style-type: decimal; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent;\">\r\n		<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n			<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">Build, maintain and manage a sales pipeline</span></span><a href=\"https://www.flickr.com/photos/sylvar/3186783884\" style=\"font-family: Arial, Verdana, sans-serif; font-size: 12px; text-align: right; background-color: transparent;\" title=\"Does &quot;sale&quot; mean lower prices or does it mean &quot;get outta here&quot;? by sylvar, on Flickr\"><img alt=\"Does &quot;sale&quot; mean lower prices or does it mean &quot;get outta here&quot;?\" height=\"180\" src=\"https://farm4.staticflickr.com/3301/3186783884_1912928de8_m.jpg\" style=\"float: right;\" width=\"240\" /></a></p>\r\n	</li>\r\n	<li dir=\"ltr\" style=\"list-style-type: decimal; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent;\">\r\n		<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n			<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">Demo and talk about our work, culture</span></span></p>\r\n	</li>\r\n</ol>\r\n<h2 dir=\"ltr\" style=\"line-height:1.15;margin-top:10pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"font-size: 17px; font-family: \'Trebuchet MS\'; color: rgb(0, 0, 0); vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">Requirements</span></span></h2>\r\n<ol style=\"margin-top:0pt;margin-bottom:0pt;\">\r\n	<li dir=\"ltr\" style=\"list-style-type: decimal; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent;\">\r\n		<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n			<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">Experience in business development</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\" style=\"list-style-type: decimal; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent;\">\r\n		<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n			<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">Outgoing personality and you enjoy connecting with people</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\" style=\"list-style-type: decimal; font-size: 15px; font-family: Arial; color: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent;\">\r\n		<p dir=\"ltr\" style=\"line-height:1.15;margin-top:0pt;margin-bottom:0pt;\">\r\n			<span id=\"docs-internal-guid-d0f6a6e4-b545-296d-ee39-54f16f48bd95\"><span style=\"vertical-align: baseline; white-space: pre-wrap; background-color: transparent;\">Attention to detail and a sense of urgency to complete tasks</span></span></p>\r\n	</li>\r\n</ol>\r\n<p>\r\n	<a class=\"btn btn-default btn-primary\" href=\"mailto:bootstrap@causecode.com?subject=Want%20to%20join%20the%20team!\">Apply</a></p>','2014-10-04 08:58:04','Boston, MA','Sales Executive - Part Time',0);
